import { useRouter } from 'next/router';
import { node, string, bool } from 'prop-types';
import { NextSeo as NextSEO } from 'next-seo';
import Nav from '../organisms/nav';
import Footer from '../organisms/footer';

const Page = ({ children, title, description, showNav = true }) => {
	const router = useRouter();
	const url = `https://${process.env.DEFAULT_DOMAIN}${router.pathname}`;
	const fullTitle = title && `${title} â€“ k4m4`;
	const cardURL = `https://cards.microlink.io/?p=2gKXPD4KICA8TGluawogICAgaHJlZj0naHR0cHM6Ly9mb250cy5nb29nbGVhcGlzLmNvbS9jc3M_--CiAgPC9GbGV4Pgo8Lz4K&title=${title}`;
	const openGraph = {
		title: fullTitle,
		description,
		url,
	};

	if (title) {
		openGraph.images = [{
			url: `https://i.microlink.io/${encodeURIComponent(cardURL)}`,
			alt: title,
			width: 1686,
			height: 948,
		}];
	}

	return (
		<>
			<NextSEO
				title={fullTitle}
				description={description}
				canonical={url}
				openGraph={openGraph}
			/>
			{showNav && <Nav />}
			<main>{children}</main>
			<Footer />
		</>
	);
};

Page.propTypes = {
	children: node.isRequired,
	title: string,
	description: string,
	showNav: bool,
};

export default Page;
