import { Box } from '@chakra-ui/core';
import { NextSeo as NextSEO } from 'next-seo';
import type { OpenGraph } from 'next-seo/lib/types';
import { useRouter } from 'next/router';
import { ReactNode } from 'react';
import Footer from '../organisms/footer';
import Nav from '../organisms/nav';

type PageProps = {
	children: ReactNode;
	title?: string;
	description?: string;
	showNav?: boolean;
};

const Page = ({
	children,
	title,
	description,
	showNav = true,
}: PageProps) => {
	const router = useRouter();
	const url = `https://${process.env.DEFAULT_DOMAIN}${router.pathname}`;
	const fullTitle = title && `${title} â€“ k4m4`;
	const cardURL = `https://cards.microlink.io/?p=2gKXPD4KICA8TGluawogICAgaHJlZj0naHR0cHM6Ly9mb250cy5nb29nbGVhcGlzLmNvbS9jc3M_--CiAgPC9GbGV4Pgo8Lz4K&title=${title}`;
	const openGraph: OpenGraph = {
		title: fullTitle,
		description,
		url,
	};

	if (title) {
		openGraph.images = [{
			url: `https://i.microlink.io/${encodeURIComponent(cardURL)}`,
			alt: title,
			width: 1686,
			height: 948,
		}];
	}

	return (
		<>
			<NextSEO
				canonical={url}
				description={description}
				openGraph={openGraph}
				title={fullTitle}
			/>
			{showNav && <Nav />}
			<Box as="main">
				{children}
			</Box>
			<Footer />
		</>
	);
};

export default Page;
